import { Component } from "@angular/core";
import { Entidades } from "src/app/models/Entidades";




@Component({
  selector: 'app-entidades',
  templateUrl: 'entidades.component.html'
})
export class EntidadesComponent {
  private amount: number = 0;
  entidades: Entidades[] = []
  /* public listarEntidades(): void; */
  /* private listarEntidades: void = async () => {

  } */
  listarEntidades(): Entidades[] {
    let lista: Entidades[] = []

    fetch('http://localhost:8090/entidad')
      .then((response: Response) => response.json())
      .then((data: Entidades[]) => data.forEach(e => {
        lista.push(e);
      }))
      console.log(lista)
    return lista;
  }
}

<p>Las entidades {{ entidades() }}</p>
<table>
  <thead>
    <th>ID</th>
    <th>TIPO DOCUMENTO</th>
    <th>NUM. DOCUMENTO</th>
    <th>RAZON SOCIAL</th>
    <th>NOMBRE COMERCIAL</th>
    <th>TIPO CONTRIBUYENTE</th>
    <th>DIRECCION</th>
    <th>TELEFONO</th>
    <th>ESTADO</th>
  </thead>
</table>
